CREATE TABLE `mission` (
                           `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键',
                           `name` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '任务名',
                           `business_type` int DEFAULT NULL COMMENT '业务类型',
                           `mission_type` bigint DEFAULT NULL COMMENT '任务类型',
                           `mission_type_l2` bigint DEFAULT NULL COMMENT '任务二级类型',
                           `start_time` datetime DEFAULT NULL COMMENT '开始时间',
                           `end_time` datetime DEFAULT NULL COMMENT '结束时间',
                           `finish_time` datetime DEFAULT NULL COMMENT '完成时间',
                           `status` int DEFAULT NULL COMMENT '状态',
                           `create_mis_code` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '创建人misCode',
                           `ctime` datetime DEFAULT NULL COMMENT '创建时间',
                           `update_mis_code` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '更新人',
                           `utime` datetime DEFAULT NULL COMMENT '更新时间',
                           `remark` varchar(1000) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '任务说明',
                           `sort_order` int NOT NULL DEFAULT '0' COMMENT '与状态一起绑定的排序值',
                           `create_mis_name` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '创建人姓名',
                           `mission_object_type` int NOT NULL DEFAULT '0' COMMENT '任务类型：BD、团长、大蜂窝、小蜂窝',
                           `collection_type` int NOT NULL DEFAULT '0' COMMENT 'bd收集反馈信息的方式，0表示bd反馈的方式，1表示团长拜访方式',
                           `quota_type` int NOT NULL DEFAULT '0' COMMENT '0表示无指标，1表示关团预警，2表示AOI点亮指标',
                           `tags` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'App端展示的tag，无什么实际意义',
                           `priority` int DEFAULT '40' COMMENT '10-紧急，20-高优，30-中优，40-普通',
                           `mission_monitor_type` int DEFAULT NULL COMMENT '任务监控类型，0-处理，1-处理加指标，2-指标',
                           `predict_process_rate` int DEFAULT NULL COMMENT '预估处理率',
                           `show_finish_view_type` int DEFAULT NULL,
                           `bd_transfer` int DEFAULT NULL COMMENT '是否自动变更BD，0-不变更，1-变更',
                           `plan_id` bigint DEFAULT NULL COMMENT '计划id',
                           `min_process_threshold` int DEFAULT NULL COMMENT 'BD工作过程阀值',
                           `strategy_id` bigint DEFAULT NULL COMMENT '策略id',
                           `min_quota_threshold` int DEFAULT NULL COMMENT 'BD工作包结果阀值',
                           PRIMARY KEY (`id`),
                           KEY `end_time_index` (`end_time`) USING BTREE COMMENT '结束时间索引，定时任务',
                           KEY `ctime_index` (`ctime`) USING BTREE COMMENT '任务创建时间索引',
                           KEY `mission_name_index` (`name`) USING BTREE COMMENT '任务名索引'
) ENGINE=InnoDB AUTO_INCREMENT=14824 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='任务基本信息表'


